package ch.bbw.zork;/* * author:  Michael Kolling, Version: 1.0, Date: July 1999 * refactoring: Rinaldo Lanza, September 2020 * refactoring Aaron Holenstein, Januar 2022 */import java.io.BufferedReader;import java.io.IOException;import java.io.InputStream;import java.io.InputStreamReader;public class Parser {    private final CommandWords validCommandWords;    private final InputStream inputStream;    public Parser(InputStream inputStream) {        this.inputStream = inputStream;        this.validCommandWords = new CommandWords();    }    public Command getCommand() {        String inputLine;        System.out.print("> ");        BufferedReader bufferedReader = new BufferedReader(new InputStreamReader(this.inputStream));        try {            inputLine = bufferedReader.readLine();        } catch (IOException e) {            System.out.println("Something went wrong");            return null;        }        String[] tokens = inputLine.split(" ");        if (tokens.length == 1) {            if (validCommandWords.isCommand(tokens[0])) {                return new Command(tokens[0]);            }        } else if (tokens.length == 2) {            if (validCommandWords.isCommand(tokens[0])) {                return new Command(tokens[0], tokens[1]);            }        }        return null;    }    public String showCommands() {        return validCommandWords.showAll();    }}